import Home from "./pages/Home";
import About from "./pages/About";
import Contact from "./pages/Contact";
import InstagramIcon from "@mui/icons-material/Instagram";
import { BrowserRouter, Link, Route, Routes } from "react-router-dom";
import Shop from "./pages/Shop";
import ProductScreen from "./pages/ProductScreen";
import Navbar from "react-bootstrap/Navbar";
import Badge from "react-bootstrap/Badge";
import Nav from "react-bootstrap/Nav";
import Container from "react-bootstrap/Container";
import { LinkContainer } from "react-router-bootstrap";
import { useContext } from "react";
import { Store } from "./Store";
import CartScreen from "./pages/CartScreen";
import SignInScreen from "./pages/SignInScreen";
import NavDropdown from "react-bootstrap/NavDropdown";
import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import ShippingAddress from "./pages/ShippingAddress";
import SignUpScreen from "./pages/SignUpScreen";
import PaymentMethod from "./pages/PaymentMethod";
import PlaceOrderScreen from "./pages/PlaceOrderScreen";
import OrderScreen from "./pages/OrderScreen";
import OrderHistoryScreen from "./pages/OrderHistoryScreen";
import ProfileScreen from "./pages/ProfileScreen";

function App() {
  const { state, dispatch: ctxDispatch } = useContext(Store);
  const { cart, userInfo } = state;

  const signoutHandler = () => {
    ctxDispatch({ type: "USER_SIGNOUT" });
    localStorage.removeItem("userInfo");
    localStorage.removeItem("shippingAddress");
    localStorage.removeItem("paymentMethod");
  };

  return (
    <BrowserRouter>
      <div className="d-flex flex-column site-container">
        <ToastContainer position="bottom-center" limit={1} />
        <header>
          <Navbar bg="dark" variant="dark" expand="lg">
            <Container>
              <LinkContainer to="/">
                <Navbar.Brand>Bart Sabbath X GMora Thrift</Navbar.Brand>
              </LinkContainer>
              <Navbar.Toggle arias-aria-controls="basic-navbar-nav" />
              <Navbar.Collapse id="basic-navbar-nav">
                <Nav className="me-auto w-100 justify-content-end">
                  <Link to="/" className="nav-link">
                    Home
                  </Link>
                  <Link to="/Shop" className="nav-link">
                    Shop
                  </Link>
                  <Link to="/About" className="nav-link">
                    About
                  </Link>
                  <Link to="/Contact" className="nav-link">
                    Contact
                  </Link>
                  <Link to="/cart" className="nav-link">
                    Cart
                    {cart.cartItems.length > 0 && (
                      <Badge pill bg="danger">
                        {cart.cartItems.reduce((a, c) => a + c.quantity, 0)}
                      </Badge>
                    )}
                  </Link>
                  {userInfo ? (
                    <NavDropdown title={userInfo.name} id="basic-nav-dropdown">
                      <LinkContainer to="/profile">
                        <NavDropdown.Item>User Profile</NavDropdown.Item>
                      </LinkContainer>
                      <LinkContainer to="/orderhistory">
                        <NavDropdown.Item>Order History</NavDropdown.Item>
                      </LinkContainer>
                      <NavDropdown.Divider />
                      <Link
                        className="dropdown-item"
                        to="#signout"
                        onClick={signoutHandler}
                      >
                        Sign Out
                      </Link>
                    </NavDropdown>
                  ) : (
                    <Link className="nav-link" to="/signin">
                      Sign In
                    </Link>
                  )}
                </Nav>
              </Navbar.Collapse>
            </Container>
          </Navbar>
        </header>
        <main>
          <Routes>
            <Route path="/product/:slug" element={<ProductScreen />} />
            <Route path="/Shop" element={<Shop />} />
            <Route exact path="/" element={<Home />} />
            <Route exact path="/About" element={<About />} />
            <Route exact path="/Contact" element={<Contact />} />
            <Route exact path="/Cart" element={<CartScreen />} />
            <Route exact path="/signin" element={<SignInScreen />} />
            <Route exact path="/signup" element={<SignUpScreen />} />
            <Route exact path="/profile" element={<ProfileScreen />} />
            <Route exact path="/shipping" element={<ShippingAddress />} />
            <Route exact path="/payment" element={<PaymentMethod />} />
            <Route exact path="/placeorder" element={<PlaceOrderScreen />} />
            <Route exact path="/order/:id" element={<OrderScreen />} />
            <Route
              exact
              path="/orderhistory"
              element={<OrderHistoryScreen />}
            />
          </Routes>
        </main>
        <footer className="footer" variant="dark">
          <div className="socialMedia">
            <a href="https://www.instagram.com/bs.x.gm.thrift/">
              <InstagramIcon />
            </a>
          </div>
          <div className="footer-text">
            &copy; Bart Sabbath X GMora Thrift 2023
          </div>
        </footer>
      </div>
    </BrowserRouter>
  );
}
export default App;
