import React, { useEffect, useReducer, useState } from 'react'
//import ShopList from "../helpers/ShopList"
import "../styles/Shop.css"
import axios from 'axios';
import logger from 'use-reducer-logger';

//Need to research why we should use a reducer instead of use state
const reducer = (state, action) => {
  switch(action.type){
    case 'FETCH_REQUEST':
      return {...state, loading: true};
    case 'FETCH_SUCCESS':
      //action.payload contains the objects from the backend
      return {...state, products: action.payload, loading: false};
    case 'FETCH_FAIL':
      return {...state, loading: false, error: action.payload};
    //if case is not equal to any of the values below:
    default:
      return state;
  }
}

function Shop() {
  //const [products, setProducts] = useState([]);
  const [{loading, error, products}, dispatch] = useReducer(logger(reducer), {
    products: [], 
    loading: true,
    error: '',
  })
  useEffect(() => {
    const fetchShopList = async () => {
      dispatch({type: 'FETCH_REQUEST'})
      try {

        const result = await axios.get('/api/products');
        dispatch({type: 'FETCH_SUCCESS', payload: result.data})
      } catch(err)
      {
        dispatch({type: 'FETCH_FAIL', payload: err.message})
      }

      //setProducts(result.data);
    };
    fetchShopList();

  }, []);
  return (
    <div className='shop'>
        <h1 className='shopTitle'>Our Clothes</h1>
        <h2>Work in Progress...</h2>
        <div className='shopList'>
        {
          loading?<div>Loading...</div>
          :
          error? <div>{error}</div>
          :
          products.map(product => (
          <div className='product' key={product.slug}>
            <a href={`/product/${product.slug}`}>
              <img src={product.image} alt={product.name} />
            </a>
            <div className='product-info'>
              <a href={`/product/${product.slug}`}>
                <p>{product.name}</p>
              </a>
              <p><strong>${product.price}</strong></p>
              <button>Add to Cart</button>
            </div>
          </div>)) 
        }
        </div>
    </div>
  )
}

export default Shop
